    private void bntLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntLoginActionPerformed
        // pega
        
        String cpf = txtCPF.getText();
        char[] senha = txtSenha.getPassword();

        System.out.println( "Dados: "  + " " + cpf + " " + senha);

        VerificarLoginController novoLogin = new VerificarLoginController();
        novoLogin.VerificarUsuarioController(cpf, cpf, senha, this);
        
        

        txtCPF.setText("");
        txtSenha.setText("");

    }//GEN-LAST:event_bntLoginActionPerformed

    private void bntCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntCadastrarActionPerformed
        SignUp SignUpFrame = new SignUp();
        SignUpFrame.setVisible(true);
        SignUpFrame.pack();
        SignUpFrame.setLocationRelativeTo(null);
        this.dispose();
    }//GEN-LAST:event_bntCadastrarActionPerformed

